        <>
            <div className={styles.container}>
                <div className={styles.header}>
                    <img className={styles.rateImg} srs={imgRate} alt='' />
                </div>
                <div className={styles.button}>
                    {/* <SortPopup handleChange={handleChange}/>  */}
                    <button className={styles.random_button}>Random Rating</button>    
                </div>
                <div className={styles.singers}>
                    {Items.map((item) => <SingerItem singer={item} key={item.name} />)}
                </div>

            </div>
        </>
    )
}

