    }
  }, [randomRating]);

  const handleRatingChange = (event, newValue) => {
    setValue(newValue);
    onChange(newValue);
  };

  return (
    <Rating
      name="simple-controlled"
      value={value}
      onChange={handleRatingChange}
    />
  );
}


