import React, { useEffect, useSelector } from 'react';
import { useDispatch } from 'react-redux';
import styles from '../../css_modules/booklist.module.css';

import { setItems } from '../../redux/slices/ratingSlice';
import SortPopup from './SortPopUp';
import SingerItem from '../singerItem/SingerItem';

const SingerList = () => {
    const dispatch = useDispatch();
    const {items, sort} = useSelector((state) => state.ratingReducer);
    const sortItems = (items, sort) => {
        if(sort === 'By rating') {
            return items.sort((a, b) => b.rating - a.rating);
        } else {
            return items;
        }
    }

    const handleChange = (event) => {
        console.log(event.target.value);
        dispatch(setSort(event.target.value));
      };
    

    return (
        <>
            <div className={styles.container}>
                <div className={styles.header}>
                    <img className={styles.rateImg} srs={items.url} alt={items.name} />
                </div>
                <div className={styles.button}>
                    <button>Random Rating</button>    
                </div>
                <div className={styles.singers}>
                    {items.map((item) => <SingerItem singer={item} key={item.name} />)}
                </div>

            </div>
        </>
    )
}

export default SingerList;

