import React, { useEffect, useSelector } from 'react';
import { useDispatch } from 'react-redux';
import styles from '../../css_modules/booklist.module.css';

import { setSort } from '../../redux/slices/ratingSlice';
import SortPopup from './SortPopUp';
import SingerItem from '../singerItem/SingerItem';

const SingerList = () => {
    const dispatch = useDispatch();
    const sortedItems = useSelector((state) => {
        const {sort, items} = state.ratingReducer;
        console.log('sort', sort);
        if (sort === 'By rating'){
          console.log('By rating', sort);
          return items.slice().sort((a, b) => b.rating - a.rating);
        } 
        return items;
      });

    const handleChange = (event) => {
        console.log(event.target.value);
        dispatch(setSort(event.target.value));
      };
    

    return (
        <>
            <div className={styles.container}>
                <div className={styles.header}>
                    <img className={styles.rateImg} srs={items.url} alt={items.name} />
                </div>
                <div className={styles.button}>
                    <SortPopup handleChange={handleChange}/> 
                    <button>Random Rating</button>    
                </div>sortedItems?
                <div className={styles.singers}>
                    {sortedItems?.map((item) => <SingerItem singer={item} key={item.name} />)}
                </div>

            </div>
        </>
    )
}

export default SingerList;

