import React, {useState} from 'react';
import Rating from './Rating';
import styles from '../../css_modules/singeritem.module.css';
import { useDispatch } from 'react-redux';
import { setRating } from '../../redux/slices/ratingSlice';

const SingerItem = ({ singer }) => {
  const dispatch = useDispatch();

  const [randomRatingIntervalId, setRandomRatingIntervalId] = useState(null);
  const [randomRating, setRandomRating] = useState(null);

  const handleRatingChange = (ratingValue) => {
      dispatch(setRating({ id: singer.name, rating: ratingValue }));
      console.log('id', singer.name, 'rating', ratingValue)
  };

  const handleRandomRating = () => {
      if (randomRatingIntervalId) {
        clearInterval(randomRatingIntervalId);
        setRandomRatingIntervalId(null);
        setRandomRating(null);
      } else {
        const intervalId = setInterval(() => {
          const randomRating = Math.floor(Math.random() * 10) + 1;
          dispatch(setRating({ id: singer.name, rating: randomRating }));
          setRandomRating(randomRating);
        }, 1000);
        setRandomRatingIntervalId(intervalId);
      }
    };

  return (
    <div className={styles.singer}>
      <img className={styles.card} src={singer.coverUrl} alt={singer.name} />
      <div className={styles.info}>
        <div className={styles.singer_name}>{singer.name}</div>
        <div className={styles.single}>{singer.single}</div>
        <Rating stars={singer.rating} randomRating={randomRating} onChange={handleRatingChange} />
      </div>
      {/* <button className={styles.random_button} onClick={handleRandomRating}>
        {randomRatingIntervalId ? 'Stop Random Rating' : 'Start Random Rating'}
      </button> */}
    </div>
  );
};

export default SingerItem;

